	void load() throws UnknownHostException, IOException,
			ClassNotFoundException {
		beijing u = new beijing();
		ArrayList<beijing> beijings = u.viewData();
		Object detail[][] = new Object[beijings.size()][1];
		int i;
		String e;
		e = this.jTextField2.getText();
		i = Integer.parseInt(e);
		if (i < 4) {
			int a;
			String b;
			String c;
			String d;
			b = this.jTextField1.getText();
			a = Integer.parseInt(b);
			this.jTextArea1.setText(beijings.get(a).getContent());
			a = a + 1;
			c = String.valueOf(a);
			this.jTextField1.setText(c);
			i = i + 1;
			d = String.valueOf(i);
			this.jTextField2.setText(d);
		} else {
			new captian4().setVisible(true);
			this.dispose();
		}
	}

	void load1() throws UnknownHostException, IOException,
			ClassNotFoundException {
		beijing u = new beijing();
		ArrayList<beijing> beijings = u.viewData();
		Object detail[][] = new Object[beijings.size()][1];
		for (int i = beijings.size() - 1; i < beijings.size(); i++) {
			this.jTextArea1.setText(beijings.get(i - 4).getContent());
		}

	}

